@tailwind base;
@tailwind components;
@tailwind utilities;

/* LIGHT MODE (DEFAULT) */
:root {
  --button-outline: rgba(0,0,0, .10);
  --badge-outline: rgba(0,0,0, .05);

  /* Automatic computation of border around primary / danger buttons */
  --opaque-button-border-intensity: -8; /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(0,0,0, .03);
  --elevate-2: rgba(0,0,0, .08);

  /* Bright, approachable palette */
  --background: 211 33% 97%;  /* #F5F7FA - soft light neutral */

  --foreground: 210 29% 24%;  /* #2C3E50 - dark charcoal for headlines */

  --border: 217 12% 85%;  /* light gray border */

  --card: 0 0% 100%;  /* #FFFFFF - pure white cards */

  --card-foreground: 210 29% 24%;  /* dark charcoal text on cards */

  --card-border: 217 12% 85%;  /* light gray border */

  --sidebar: 0 0% 100%;  /* white sidebar */

  --sidebar-foreground: 210 29% 24%;

  --sidebar-border: 217 12% 85%;

  --sidebar-primary: 0 0% 0%;  /* black */

  --sidebar-primary-foreground: 0 0% 100%;

  --sidebar-accent: 211 33% 95%;  /* very light gray */

  --sidebar-accent-foreground: 210 29% 24%;

  --sidebar-ring: 11 100% 67%;  /* coral */

  --popover: 0 0% 100%;

  --popover-foreground: 210 29% 24%;

  --popover-border: 217 12% 85%;

  --primary: 0 0% 0%;  /* #000000 - black for buttons/headlines */

  --primary-foreground: 0 0% 100%;  /* white text on primary */

  --secondary: 42 100% 67%;  /* #FFC857 - saffron yellow */

  --secondary-foreground: 210 29% 24%;  /* dark text on secondary */

  --muted: 211 33% 95%;  /* very light background for muted sections */

  --muted-foreground: 217 12% 59%;  /* #8B95A5 - tertiary text */

  --accent: 11 100% 67%;  /* #FF7A59 - coral accent */

  --accent-foreground: 0 0% 100%;  /* white text on accent */

  --destructive: 0 84% 60%;

  --destructive-foreground: 0 0% 100%;

  --input: 217 12% 85%;
  --ring: 11 100% 67%;  /* coral */
  --chart-1: 0 0% 0%;  /* black */
  --chart-2: 11 100% 67%;  /* coral */
  --chart-3: 42 100% 67%;  /* saffron */
  --chart-4: 210 29% 24%;  /* dark charcoal */
  --chart-5: 217 12% 59%;  /* gray */

  --font-sans: Inter, sans-serif;
  --font-serif: 'Playfair Display SC', 'Playfair Display', serif;
  --font-mono: Menlo, Monaco, 'Courier New', monospace;
  --radius: 0.75rem; /* 12px for subtle rounding */

  /* Enhanced shadows for depth */
  --shadow-2xs: 0px 1px 2px 0px rgba(19, 124, 139, 0.05);
  --shadow-xs: 0px 1px 3px 0px rgba(19, 124, 139, 0.08);
  --shadow-sm: 0px 2px 4px -1px rgba(19, 124, 139, 0.06), 0px 1px 2px 0px rgba(19, 124, 139, 0.10);
  --shadow: 0px 4px 6px -1px rgba(19, 124, 139, 0.08), 0px 2px 4px -2px rgba(19, 124, 139, 0.12);
  --shadow-md: 0px 6px 12px -2px rgba(19, 124, 139, 0.10), 0px 3px 6px -3px rgba(19, 124, 139, 0.15);
  --shadow-lg: 0px 10px 20px -5px rgba(19, 124, 139, 0.12), 0px 4px 8px -4px rgba(19, 124, 139, 0.18);
  --shadow-xl: 0px 20px 30px -10px rgba(19, 124, 139, 0.15), 0px 8px 16px -8px rgba(19, 124, 139, 0.20);
  --shadow-2xl: 0px 25px 40px -15px rgba(19, 124, 139, 0.18);

  --tracking-normal: 0em;
  --spacing: 0.25rem;

  /* Automatically computed borders - intensity can be controlled by the user by the --opaque-button-border-intensity setting */

  /* Fallback for older browsers */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --primary-border: hsl(var(--primary));
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --secondary-border: hsl(var(--secondary));
  --secondary-border: hsl(from hsl(var(--secondary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --muted-border: hsl(var(--muted));
  --muted-border: hsl(from hsl(var(--muted)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --accent-border: hsl(var(--accent));
  --accent-border: hsl(from hsl(var(--accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --destructive-border: hsl(var(--destructive));
  --destructive-border: hsl(from hsl(var(--destructive)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
}

/* DARK MODE */
.dark {
  --button-outline: rgba(255,255,255, .10);
  --badge-outline: rgba(255,255,255, .05);

  --opaque-button-border-intensity: 9;  /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(255,255,255, .04);
  --elevate-2: rgba(255,255,255, .09);

  --background: 220 20% 12%;  /* #1A1F2E - charcoal */

  --foreground: 211 33% 92%;  /* light text */

  --border: 220 20% 20%;

  --card: 220 20% 16%;  /* darker charcoal cards */

  --card-foreground: 211 33% 92%;

  --card-border: 186 75% 31%;  /* teal accent border */

  --sidebar: 220 20% 14%;

  --sidebar-foreground: 211 33% 92%;

  --sidebar-border: 220 20% 20%;

  --sidebar-primary: 186 75% 31%;

  --sidebar-primary-foreground: 0 0% 100%;

  --sidebar-accent: 220 20% 20%;

  --sidebar-accent-foreground: 211 33% 92%;

  --sidebar-ring: 11 100% 67%;  /* coral */

  --popover: 220 20% 16%;

  --popover-foreground: 211 33% 92%;

  --popover-border: 220 20% 20%;

  --primary: 0 0% 0%;  /* black */

  --primary-foreground: 0 0% 100%;

  --secondary: 42 100% 67%;  /* saffron */

  --secondary-foreground: 220 20% 12%;

  --muted: 220 20% 18%;

  --muted-foreground: 217 12% 59%;

  --accent: 11 100% 67%;  /* coral */

  --accent-foreground: 0 0% 100%;

  --destructive: 0 84% 60%;

  --destructive-foreground: 0 0% 100%;

  --input: 220 20% 25%;
  --ring: 11 100% 67%;  /* coral */
  --chart-1: 0 0% 100%;  /* white */
  --chart-2: 11 100% 75%;  /* coral */
  --chart-3: 42 100% 75%;  /* saffron */
  --chart-4: 211 33% 92%;  /* light gray */
  --chart-5: 217 12% 70%;  /* gray */

  /* Darker shadows for dark mode */
  --shadow-2xs: 0px 1px 2px 0px rgba(0, 0, 0, 0.20);
  --shadow-xs: 0px 1px 3px 0px rgba(0, 0, 0, 0.25);
  --shadow-sm: 0px 2px 4px -1px rgba(0, 0, 0, 0.22), 0px 1px 2px 0px rgba(0, 0, 0, 0.30);
  --shadow: 0px 4px 6px -1px rgba(0, 0, 0, 0.25), 0px 2px 4px -2px rgba(0, 0, 0, 0.35);
  --shadow-md: 0px 6px 12px -2px rgba(0, 0, 0, 0.28), 0px 3px 6px -3px rgba(0, 0, 0, 0.40);
  --shadow-lg: 0px 10px 20px -5px rgba(0, 0, 0, 0.30), 0px 4px 8px -4px rgba(0, 0, 0, 0.45);
  --shadow-xl: 0px 20px 30px -10px rgba(0, 0, 0, 0.35), 0px 8px 16px -8px rgba(0, 0, 0, 0.50);
  --shadow-2xl: 0px 25px 40px -15px rgba(0, 0, 0, 0.40);

  /* Automatically computed borders - intensity can be controlled by the user by the --opaque-button-border-intensity setting */
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
  --secondary-border: hsl(from hsl(var(--secondary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
  --muted-border: hsl(from hsl(var(--muted)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
  --accent-border: hsl(from hsl(var(--accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
  --destructive-border: hsl(from hsl(var(--destructive)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }

  h1, h2, h3, h4, h5, h6 {
    @apply font-serif;
  }
}

/* Gradient utilities for visual accents */
@layer utilities {
  .gradient-teal-aqua {
    background: linear-gradient(135deg, #137C8B 0%, #00C4CC 100%);
  }

  .gradient-coral-peach {
    background: linear-gradient(135deg, #FF7A59 0%, #FFB199 100%);
  }

  .gradient-logo {
    background: linear-gradient(135deg, #137C8B 0%, #4DD9E8 100%);
  }

  .glassmorphism {
    background: rgba(255, 255, 255, 0.7);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.3);
  }

  .dark .glassmorphism {
    background: rgba(26, 31, 46, 0.7);
    border: 1px solid rgba(255, 255, 255, 0.1);
  }

  .accent-border-glow {
    box-shadow: 0 0 0 1px hsl(var(--accent) / 0.2), 0 0 10px hsl(var(--accent) / 0.15);
  }

  .primary-border-glow {
    box-shadow: 0 0 0 1px hsl(var(--primary) / 0.2), 0 0 10px hsl(var(--primary) / 0.15);
  }

  /* Hide ugly search cancel button in Chrome until we can style it properly */
  input[type="search"]::-webkit-search-cancel-button {
    @apply hidden;
  }

  /* Placeholder styling for contentEditable div */
  [contenteditable][data-placeholder]:empty::before {
    content: attr(data-placeholder);
    color: hsl(var(--muted-foreground));
    pointer-events: none;
  }

  /* .no-default-hover-elevate/no-default-active-elevate is an escape hatch so consumers of
   * buttons/badges can remove the automatic brightness adjustment on interactions
   * and program their own. */
  .no-default-hover-elevate {}

  .no-default-active-elevate {}


  /**
   * Toggleable backgrounds go behind the content. Hoverable/active goes on top.
   * This way they can stack/compound. Both will overlap the parent's borders!
   * So borders will be automatically adjusted both on toggle, and hover/active,
   * and they will be compounded.
   */
  .toggle-elevate::before,
  .toggle-elevate-2::before {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    border-radius: inherit;
    z-index: -1;
  }

  .toggle-elevate.toggle-elevated::before {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.toggle-elevate::before {
    inset: -1px;
  }

  /* Does not work on elements with overflow:hidden! */
  .hover-elevate:not(.no-default-hover-elevate),
  .active-elevate:not(.no-default-active-elevate),
  .hover-elevate-2:not(.no-default-hover-elevate),
  .active-elevate-2:not(.no-default-active-elevate) {
    position: relative;
    z-index: 0;
  }

  .hover-elevate:not(.no-default-hover-elevate)::after,
  .active-elevate:not(.no-default-active-elevate)::after,
  .hover-elevate-2:not(.no-default-hover-elevate)::after,
  .active-elevate-2:not(.no-default-active-elevate)::after {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    border-radius: inherit;
    z-index: 999;
  }

  .hover-elevate:hover:not(.no-default-hover-elevate)::after,
  .active-elevate:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-1);
  }

  .hover-elevate-2:hover:not(.no-default-hover-elevate)::after,
  .active-elevate-2:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate-2:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate-2:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after {
    inset: -1px;
  }
}

